<div class="container mx-auto px-2">
    <div class="w-full lg:w-large lg:mx-auto px-4 md:px-6 lg:px-0 xl:w-feLarge">
        <div class="bg-feAchromatic-50 dark_bg-feAchromatic-900 rounded-lg w-auto sm:w-[556px] sm:mt-32 lg:mt-36 mt-28 mb-16 mx-auto py-4">
            <div class="flex justify-center items-center">
                <p class="text-feSecondary-800 dark_text-feSecondary-300 sm:text-2xl text-xl font-semibold mb-4 font-medium">Agricultural support</p>
            </div>
            <div class="w-full flex flex-col">
                <div class="px-10">
                    <p class="text-feSecondary-800 dark_text-feSecondary-300 mt-4">Contact Name <span style="font-size: 17px; color: red;">*</span></p><input class="block bg-transparent w-full px-3 py-2 text-sm shadow-sm dark_placeholder-feSecondary-300 placeholder-feSecondary-500 text-feSecondary-500 dark_text-feSecondary-400 rounded border border-feSecondary-200 hover_border-feSecondary-400 dark_border-feSecondary-400 hover:dark_border-feSecondary-50 focus:outline-none focus_border-none focus_ring-2 focus_ring-fePrimary-300 ring-fePrimary-300 placeholder-feSecondary-500 dark_placeholder-feSecondary-400 opacity-100 mt-2" type="text" maxlength="99999999" placeholder="Contact Name"><!---->
                </div>
                <div class="px-10">
                    <p class="text-feSecondary-800 dark_text-feSecondary-300 mt-4">Contact Phone <span style="font-size: 17px; color: red;">*</span></p><input class="block bg-transparent w-full px-3 py-2 text-sm shadow-sm dark_placeholder-feSecondary-300 placeholder-feSecondary-500 text-feSecondary-500 dark_text-feSecondary-400 rounded border border-feSecondary-200 hover_border-feSecondary-400 dark_border-feSecondary-400 hover:dark_border-feSecondary-50 focus:outline-none focus_border-none focus_ring-2 focus_ring-fePrimary-300 ring-fePrimary-300 placeholder-feSecondary-500 dark_placeholder-feSecondary-400 opacity-100 mt-2" type="text" maxlength="99999999" placeholder="Contact Phone"><!---->
                </div>
                <div class="px-10">
